import{_ as t}from"./IconInterface-8ea410a6.js";import{_ as o,r as e,o as p,c,b as n,d as s,a as l,e as i}from"./app-9cc97c28.js";const u={},d=n("h2",{id:"内置图标",tabindex:"-1"},[n("a",{class:"header-anchor",href:"#内置图标","aria-hidden":"true"},"#"),s(" 内置图标")],-1),r=n("p",null,"QFluentWidgets 中的组件一般支持以下三种图标参数类型：",-1),k=n("ul",null,[n("li",null,[n("code",null,"str"),s(": 图标路径")]),n("li",null,[n("code",null,"QIcon"),s("：Qt 图标")]),n("li",null,[n("code",null,"FluentIconBase"),s(": 流畅图标抽象类")])],-1),m=n("code",null,"FluentIcon",-1),v=n("code",null,"FluentIconBase",-1),b={href:"https://github.com/zhiyiYo/PyQt-Fluent-Widgets/releases/download/v1.1.6/PyQt-Fluent-Widgets-Gallery_v1.1.6_lite_windows_x64.zip",target:"_blank",rel:"noopener noreferrer"},h=n("img",{src:t,alt:"IconInterface"},null,-1),g=i(`<h2 id="适配-qt-原生组件" tabindex="-1"><a class="header-anchor" href="#适配-qt-原生组件" aria-hidden="true">#</a> 适配 Qt 原生组件</h2><p>对于不支持 <code>FluentIconBase</code> 的类，比如只支持 <code>QIcon</code> 的 <code>QListWidgetItem</code>，可以调用 <code>FluentIconBase.qicon()</code> 返回一个能够自动切换主题的 <code>QIcon</code>。</p><h2 id="自定义图标" tabindex="-1"><a class="header-anchor" href="#自定义图标" aria-hidden="true">#</a> 自定义图标</h2><h3 id="更改颜色" tabindex="-1"><a class="header-anchor" href="#更改颜色" aria-hidden="true">#</a> 更改颜色</h3><p>流畅图标基类提供了 <code>FluentIconBase.icon()</code> 方法:</p><div class="language-python line-numbers-mode" data-ext="py"><pre class="language-python"><code><span class="token keyword">def</span> <span class="token function">icon</span><span class="token punctuation">(</span>self<span class="token punctuation">,</span> theme<span class="token operator">=</span>Theme<span class="token punctuation">.</span>AUTO<span class="token punctuation">,</span> color<span class="token punctuation">:</span> QColor <span class="token operator">=</span> <span class="token boolean">None</span><span class="token punctuation">)</span> <span class="token operator">-</span><span class="token operator">&gt;</span> QIcon<span class="token punctuation">:</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>可以根据传入的主题或者颜色返回一个 <code>QIcon</code> 图标，只有在图标类型为 svg 图标时，<code>color</code> 参数才会起作用。下面给出一个示例：</p><div class="language-python line-numbers-mode" data-ext="py"><pre class="language-python"><code><span class="token comment"># 使用浅色主题图标</span>
button <span class="token operator">=</span> ToolButton<span class="token punctuation">(</span>FluentIcon<span class="token punctuation">.</span>ADD<span class="token punctuation">.</span>icon<span class="token punctuation">(</span>Theme<span class="token punctuation">.</span>LIGHT<span class="token punctuation">)</span><span class="token punctuation">)</span>

<span class="token comment"># 使用深色主题图标</span>
button <span class="token operator">=</span> ToolButton<span class="token punctuation">(</span>FluentIcon<span class="token punctuation">.</span>ADD<span class="token punctuation">.</span>icon<span class="token punctuation">(</span>Theme<span class="token punctuation">.</span>DARK<span class="token punctuation">)</span><span class="token punctuation">)</span>

<span class="token comment"># 使用颜色名称</span>
button <span class="token operator">=</span> ToolButton<span class="token punctuation">(</span>FluentIcon<span class="token punctuation">.</span>ADD<span class="token punctuation">.</span>icon<span class="token punctuation">(</span>color<span class="token operator">=</span><span class="token string">&#39;red&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

<span class="token comment"># 使用十六进制字符串</span>
button <span class="token operator">=</span> ToolButton<span class="token punctuation">(</span>FluentIcon<span class="token punctuation">.</span>ADD<span class="token punctuation">.</span>icon<span class="token punctuation">(</span>color<span class="token operator">=</span><span class="token string">&#39;#ff0000&#39;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

<span class="token comment"># 使用 Qt.GlobalColor</span>
button <span class="token operator">=</span> ToolButton<span class="token punctuation">(</span>FluentIcon<span class="token punctuation">.</span>ADD<span class="token punctuation">.</span>icon<span class="token punctuation">(</span>color<span class="token operator">=</span>Qt<span class="token punctuation">.</span>red<span class="token punctuation">)</span><span class="token punctuation">)</span>

<span class="token comment"># 使用 QColor</span>
button <span class="token operator">=</span> ToolButton<span class="token punctuation">(</span>FluentIcon<span class="token punctuation">.</span>ADD<span class="token punctuation">.</span>icon<span class="token punctuation">(</span>color<span class="token operator">=</span>QColor<span class="token punctuation">(</span><span class="token number">255</span><span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">)</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>流畅图标基类提供了 <code>FluentIconBase.colored()</code> 方法来自定义深色和浅色模式下的图标颜色：</p><div class="language-python line-numbers-mode" data-ext="py"><pre class="language-python"><code><span class="token keyword">def</span> <span class="token function">colored</span><span class="token punctuation">(</span>self<span class="token punctuation">,</span> lightColor<span class="token punctuation">:</span> QColor<span class="token punctuation">,</span> darkColor<span class="token punctuation">:</span> QColor<span class="token punctuation">)</span> <span class="token operator">-</span><span class="token operator">&gt;</span> ColoredFluentIcon<span class="token punctuation">:</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div></div></div><p>下述代码返回了一个浅色模式下为红色，深色模式下为蓝色的图标：</p><div class="language-python line-numbers-mode" data-ext="py"><pre class="language-python"><code>icon <span class="token operator">=</span> FluentIcon<span class="token punctuation">.</span>ADD<span class="token punctuation">.</span>colored<span class="token punctuation">(</span>QColor<span class="token punctuation">(</span><span class="token number">255</span><span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">)</span><span class="token punctuation">,</span> QColor<span class="token punctuation">(</span><span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">0</span><span class="token punctuation">,</span> <span class="token number">255</span><span class="token punctuation">)</span><span class="token punctuation">)</span>
button<span class="token punctuation">.</span>setIcon<span class="token punctuation">(</span>icon<span class="token punctuation">)</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="添加图标" tabindex="-1"><a class="header-anchor" href="#添加图标" aria-hidden="true">#</a> 添加图标</h3><h4 id="svg-图标" tabindex="-1"><a class="header-anchor" href="#svg-图标" aria-hidden="true">#</a> SVG 图标</h4><p>如果想在切换主题时自动更换图标，可以继承 <code>FluentIconBase</code> 类并重写 <code>path()</code> 函数来给出不同主题下图标的路径。下面是一个示例：</p><div class="language-python line-numbers-mode" data-ext="py"><pre class="language-python"><code><span class="token keyword">from</span> enum <span class="token keyword">import</span> Enum

<span class="token keyword">from</span> qfluentwidgets <span class="token keyword">import</span> getIconColor<span class="token punctuation">,</span> Theme<span class="token punctuation">,</span> FluentIconBase


<span class="token keyword">class</span> <span class="token class-name">MyFluentIcon</span><span class="token punctuation">(</span>FluentIconBase<span class="token punctuation">,</span> Enum<span class="token punctuation">)</span><span class="token punctuation">:</span>
    <span class="token triple-quoted-string string">&quot;&quot;&quot; Custom icons &quot;&quot;&quot;</span>

    ADD <span class="token operator">=</span> <span class="token string">&quot;Add&quot;</span>
    CUT <span class="token operator">=</span> <span class="token string">&quot;Cut&quot;</span>
    COPY <span class="token operator">=</span> <span class="token string">&quot;Copy&quot;</span>

    <span class="token keyword">def</span> <span class="token function">path</span><span class="token punctuation">(</span>self<span class="token punctuation">,</span> theme<span class="token operator">=</span>Theme<span class="token punctuation">.</span>AUTO<span class="token punctuation">)</span><span class="token punctuation">:</span>
        <span class="token comment"># getIconColor() 根据主题返回字符串 &quot;white&quot; 或者 &quot;black&quot;</span>
        <span class="token keyword">return</span> <span class="token string-interpolation"><span class="token string">f&#39;:/icons/</span><span class="token interpolation"><span class="token punctuation">{</span>self<span class="token punctuation">.</span>value<span class="token punctuation">}</span></span><span class="token string">_</span><span class="token interpolation"><span class="token punctuation">{</span>getIconColor<span class="token punctuation">(</span>theme<span class="token punctuation">)</span><span class="token punctuation">}</span></span><span class="token string">.svg&#39;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>之后就可以将 <code>MyFluentIcon</code> 实例作为参数传给需要图标的组件，比如：</p><div class="language-python line-numbers-mode" data-ext="py"><pre class="language-python"><code><span class="token keyword">from</span> qfluentwidgets <span class="token keyword">import</span> ToolButton<span class="token punctuation">,</span> toggleTheme

<span class="token comment"># 创建工具按钮</span>
button <span class="token operator">=</span> ToolButton<span class="token punctuation">(</span>MyFluentIcon<span class="token punctuation">.</span>ADD<span class="token punctuation">)</span>

<span class="token comment"># 更换图标</span>
button<span class="token punctuation">.</span>setIcon<span class="token punctuation">(</span>MyFluentIcon<span class="token punctuation">.</span>CUT<span class="token punctuation">)</span>

<span class="token comment"># 切换主题，图标也会跟着改变</span>
button<span class="token punctuation">.</span>clicked<span class="token punctuation">.</span>connect<span class="token punctuation">(</span>toggleTheme<span class="token punctuation">)</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h4 id="图标字体" tabindex="-1"><a class="header-anchor" href="#图标字体" aria-hidden="true">#</a> 图标字体</h4><p>组件库支持使用图标字体，可以继承 <code>FluentFontIconBase</code> 类并重写 <code>path()</code> 函数来给出图标字体的路径。下面是一个示例：</p><div class="language-python line-numbers-mode" data-ext="py"><pre class="language-python"><code><span class="token keyword">class</span> <span class="token class-name">PhotoFontIcon</span><span class="token punctuation">(</span>FluentFontIconBase<span class="token punctuation">)</span><span class="token punctuation">:</span>
    <span class="token triple-quoted-string string">&quot;&quot;&quot; Custom icon font icon &quot;&quot;&quot;</span>

    <span class="token keyword">def</span> <span class="token function">path</span><span class="token punctuation">(</span>self<span class="token punctuation">,</span> theme<span class="token operator">=</span>Theme<span class="token punctuation">.</span>AUTO<span class="token punctuation">)</span><span class="token punctuation">:</span>
        <span class="token keyword">return</span> <span class="token string">&quot;/path/to/font.ttf&quot;</span>

    <span class="token keyword">def</span> <span class="token function">iconNameMapPath</span><span class="token punctuation">(</span>self<span class="token punctuation">)</span><span class="token punctuation">:</span>
        <span class="token triple-quoted-string string">&quot;&quot;&quot; 如果想使用 \`fromName\` 来创建图标，需要重写此方法 &quot;&quot;&quot;</span>
        <span class="token keyword">return</span> <span class="token string">&quot;/path/to/fontNameMap.json&quot;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p><code>iconNameMapPath()</code> 给出了图标名称到图标码点的映射表文件路径，如果不想通过 <code>FluentFontIconBase.fromName()</code> 来创建图标，则无需重写此函数。映射表文件的格式如下：</p><div class="language-json line-numbers-mode" data-ext="json"><pre class="language-json"><code><span class="token punctuation">{</span>
    <span class="token property">&quot;cloud&quot;</span><span class="token operator">:</span> <span class="token string">&quot;\\ue753&quot;</span><span class="token punctuation">,</span>
    <span class="token property">&quot;filter&quot;</span><span class="token operator">:</span> <span class="token string">&quot;\\ue71c&quot;</span><span class="token punctuation">,</span>
    <span class="token property">&quot;smile&quot;</span><span class="token operator">:</span> <span class="token string">&quot;\\ue76e&quot;</span>
<span class="token punctuation">}</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><p>下面是图标字体的使用示例：</p><div class="language-python line-numbers-mode" data-ext="py"><pre class="language-python"><code><span class="token comment"># 使用码点创建图标</span>
button <span class="token operator">=</span> ToolButton<span class="token punctuation">(</span>PhotoFontIcon<span class="token punctuation">(</span><span class="token string">&quot;\\ue77b&quot;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

<span class="token comment"># 使用名称来创建图标</span>
button <span class="token operator">=</span> ToolButton<span class="token punctuation">(</span>PhotoFontIcon<span class="token punctuation">.</span>fromName<span class="token punctuation">(</span><span class="token string">&quot;smile&quot;</span><span class="token punctuation">)</span><span class="token punctuation">)</span>

<span class="token comment"># 自定义图标颜色</span>
button <span class="token operator">=</span> ToolButton<span class="token punctuation">(</span>PhotoFontIcon<span class="token punctuation">.</span>fromName<span class="token punctuation">(</span><span class="token string">&quot;cloud&quot;</span><span class="token punctuation">)</span><span class="token punctuation">.</span>colored<span class="token punctuation">(</span><span class="token string">&quot;#275EFF&quot;</span><span class="token punctuation">,</span> Qt<span class="token punctuation">.</span>GlobalColor<span class="token punctuation">.</span>darkCyan<span class="token punctuation">)</span><span class="token punctuation">)</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><h3 id="视频教程" tabindex="-1"><a class="header-anchor" href="#视频教程" aria-hidden="true">#</a> 视频教程</h3>`,26),y=n("div",{style:{padding:"56.25% 0 0 0",position:"relative"}},[n("iframe",{src:"https://player.vimeo.com/video/1061710278?h=d643e2b0da&badge=0&autopause=0&player_id=0&app_id=58479",frameborder:"0",allow:"autoplay; fullscreen; picture-in-picture; clipboard-write; encrypted-media",style:{position:"absolute",top:"0",left:"0",width:"100%",height:"100%"},title:"PyQt-Fluent-Widgets 自定义图标的正确姿势"},`
    `)],-1);function q(f,I){const a=e("ExternalLinkIcon");return p(),c("div",null,[d,r,k,n("p",null,[s("QFluentWidgets 提供的 "),m,s(" 继承自 "),v,s(", 包含数百个矢量图标，可以在 "),n("a",b,[s("gallery"),l(a)]),s(" 示例的图标界面查看和搜索图标。 "),h]),g,y])}const x=o(u,[["render",q],["__file","index.html.vue"]]);export{x as default};
